import React, { Component} from 'react'
import { Link } from 'react-router-dom'

export default class Newsitem extends Component {

    render() {
        let {title, description, imageUrl, newsUrl, author, date} = this.props
        return (
            <div className='my-3'>
                <div className="card">
                    <img src={imageUrl? imageUrl:"https://lh3.googleusercontent.com/J6_coFbogxhRI9iM864NL_liGXvsQp2AupsKei7z0cNNfDvGUmWUy20nuUhkREQyrpY4bEeIBuc=s0-w300-rw"} className="card-img-top" alt="..."/>
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{description}</p>
                            <p class="card-text"><small class="text-muted">By {author} on {date}</small></p>
                            <Link to={newsUrl} target='_blank' rel='noopener' className="btn btn-sm btn-dark">Read More</Link>
                        </div>
                </div>
            </div>
        )
    }
}
